import { Button } from 'react-native-elements';
import React,{useState} from "react";
import { View } from "react-native";

export default function BodyGoals({route,navigation}){
    const {tdee,lose,gain} = route.params;
    const [meal_plan,setMealplan] = useState();
   function getDiet(data){
        fetch(`https://api.spoonacular.com/mealplanner/generate?timeFrame=week&apiKey=0e5f3b97a15746b4b5d2b2d5ac294240&targetCalories=${data}`)
          .then((response) => response.json())
          .then((json) => {
        setMealplan(json);

        })
          .catch((error) => console.error(error));
    }
    return(
        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
        <Button title='Lose Weight' raised type="outline"onPress={async ()=>{
           await getDiet(lose);
           console.log(meal_plan);
            navigation.navigate('Macrodetails', {value:`${lose}`,mealplan:meal_plan})}} />
        <Button title='Gain Weight' raised type="outline"onPress={()=>{navigation.navigate('Macrodetails',{value: `${gain}`,goal:"gain"})}} />
        <Button title='Maintain Weight' raised type="outline"onPress={()=>{navigation.navigate('Macrodetails',{value: `${tdee}`,goal:"maintain"})}} />
        </View>
    );
}
