export default function Macrodetails({route,navigation}){
    const {mealplan,goal} = route.params;
    const styles = StyleSheet.create({
        container: {
          flex: 1,
        },
        scrollView: {
        //   backgroundColor: 'pink',
          marginHorizontal: 20,
          display: 'flex',
        },
