import React,{ useState } from 'react';
import { ListItem, Avatar,Icon } from 'react-native-elements'
import { Button } from 'react-native-elements';
import { View } from "react-native";
export default function ListMeals({route,navigation}){
    const {mealplan,value} = route.params;
    var today = new Date();
      var day = today.getDay();
      var daylist = ["sunday","monday","tuesday","wednesday ","thursday","friday","saturday"];
      var tday = daylist[day];
      const mealplan_now = mealplan.week[tday];
    const list = [
        {
          name: 'Amy Farha',
          subtitle: 'Vice President'
        },
        {
          name: 'Chris Jackson',
          avatar_url: 'https://s3.amazonaws.com/uifaces/faces/twitter/adhamdannaway/128.jpg',
          subtitle: 'Vice Chairman'
        }
      ];
    const [mealexpanded,setMealexpanded] = useState(false); 
    const [workoutexpanded,setWorkoutexpanded] = useState(false); 
    return(

        // <Button>Meal-Plan</Button>
        // <Button>Workout-Plan</Button>
        <View>
    <ListItem.Accordion
  content={
    <>
      <ListItem.Content>
        <ListItem.Title>Meal-plan</ListItem.Title>
      </ListItem.Content>
    </>
  }
  isExpanded={mealexpanded}
  onPress={() => {
    setMealexpanded(!mealexpanded);
    setWorkoutexpanded(false);
  }}
>
  {mealplan_now.meals.map((item) => (
    <ListItem key={item.id}  bottomDivider>
      {/* <Avatar title={l.name[0]} source={{ uri: l.avatar_url }} /> */}
      <ListItem.Content>
        <ListItem.Title>{item.title}</ListItem.Title>
        {/* <ListItem.Subtitle>{l.subtitle}</ListItem.Subtitle> */}
      </ListItem.Content>
      <ListItem.Chevron />
    </ListItem>
  ))}
</ListItem.Accordion>
<ListItem.Accordion
  content={
    <>
      <ListItem.Content>
        <ListItem.Title>Workout</ListItem.Title>
      </ListItem.Content>
    </>
  }
  isExpanded={workoutexpanded}
  onPress={() => {
    setWorkoutexpanded(!workoutexpanded);
    setMealexpanded(false);
  }}
>
  {list.map((l, i) => (
    <ListItem key={i}  bottomDivider>
      {/* <Avatar title={l.name[0]} source={{ uri: l.avatar_url }} /> */}
      <ListItem.Content>
        <ListItem.Title>{l.name}</ListItem.Title>
        <ListItem.Subtitle>{l.subtitle}</ListItem.Subtitle>
      </ListItem.Content>
      <ListItem.Chevron />
    </ListItem>
  ))}
</ListItem.Accordion>
</View> 
);
}
